 */

/* Dependencies */

/* Util */
import warn from '../util/warn'

export default class Copiable {
  #usesFallbacks
  #computedClipboard
  #computedSucceeded
  #computedErrored

  constructor (string, options = {}) {
    /* Options */
    options = {
      usesFallbacks: false,
      ...options,
    }
    this.#usesFallbacks = options.usesFallbacks

    /* Public properties */
    this.string = string

    /* Private properties */
    this.#computedClipboard = navigator.clipboard
    this.#computedSucceeded = false
    this.#computedErrored = false
    /* Dependency */
  }

  /* Public getters */
  get clipboard () {
    return this.#computedClipboard
  }
  get copied () {
    return this.#getCopied
  }
  get succeeded () {
    return this.#computedSucceeded
  }
  get errored () {
    return this.#computedErrored
  }

  /* Public methods */
  setString (string) {
    this.string = string
    return this
  }
  copy () {
    if (this.#usesFallbacks) {
      this.#writeTextFallback()
      this.#computedErrored = false
      this.#computedSucceeded = true
    } else {
      this.#writeText()
        .then(() => {
          this.#computedErrored = false
          this.#computedSucceeded = true
        })
        .catch(() => {
          this.#computedErrored = true
          this.#computedSucceeded = false
        })
    }

    return this
  }

  /* Private methods */
  #getCopied = function() {
    if (this.#usesFallbacks) {
      warn('noFallbackAvailable', {
        subject: 'Copiable\'s copied property'
      })
    } else {
      return this.#readText()
        .then(text => text)
    }
  }
  #readText = function() {
    return this.clipboard.readText()
  }
  #writeText = function() {
    return this.clipboard.writeText(this.string)
  }
  #writeTextFallback = function() {
    const input = document.createElement('input')
    input.type = 'text'
    input.value = this.string

    document.body.appendChild(input)
    input.select()
    document.execCommand('copy')

    document.body.removeChild(input)
  }
}

/*
 * Delayable.js
 * (c) 2019 Alex Vipond
 * Released under the MIT license
 */

/* Util */
import is from '../util/is'

/**
 * Delayable is a library that enriches a function by:
 * - Giving it the methods necessary to execute itself after a delay or at regular intervals<
 * - Allowing it to store the time elapsed since it was delayed, the time remaining until it will be executed, and the number of times it has been executed
 * Delayable depends on `[setTimeout](https://developer.mozilla.org/en-US/docs/Web/API/WindowOrWorkerGlobalScope/setTimeout)`, `[setInterval](https://developer.mozilla.org/en-US/docs/Web/API/WindowOrWorkerGlobalScope/setInterval)`, `[requestAnimationFrame](https://developer.mozilla.org/en-US/docs/Web/API/window/requestAnimationFrame)`, and the `[global Data object](https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Date)`, but is otherwise written in vanilla JS. Delayable powers <NuxtLink to="/docs/tools/composition-functions/useDelayable">`useDelayable`</NuxtLink>.
 */
export default class Delayable {
  #delay
  #isInterval
  #parameters
  #id
  #tickId
  #started
  #computedExecutions
  #computedTimeElapsed
  #computedTimeRemaining

  constructor (callback, options) {
    options = {
      delay: 0,
      ...options
    }

    /* Options */
    this.#delay = options.delay
    this.#parameters = is.array(options.parameters) ? options.parameters : []

    /* Public properties */
    /**
     * A shallow copy of the callback passed to the Delayable constructor
     * @type {Function}
     */
    this.callback = callback

    /* Private properties */
    this.#computedExecutions = 0
    this.#computedTimeElapsed = 0
    this.#computedTimeRemaining = this.#delay
  }

  /* Public getters */
  /**
   * The number of times the callback function has been executed
   * @type {Number}
   */
  get executions () {
    return this.#computedExecutions
  }
  /**
   * The time (in milliseconds) that has elapsed since the callback function was initially delayed OR last executed, whichever is smaller
   * @type {Number}
   */
  get timeElapsed () {
    return this.#computedTimeElapsed
  }
  /**
   * The time (in milliseconds) that remains until the callback function will be executed
   * @type {Number}
   */
  get timeRemaining () {
    return this.#computedTimeRemaining
  }

  /* Public methods */
  /**
   * Sets the Delayable instance's callback function
   * @param {Function} callback The new callback function
   */
  setCallback (callback) {
    this.callback = callback
  }
  /**
   * Clears the delayed callback function. The function won't be executed, but <code>timeElapsed</code> and <code>timeRemaining</code> will <b>not</b> be reset to their initial values.
   */
  clear () {
    window.clearTimeout(this.#id)
    window.clearInterval(this.#id)
    this.#stopTick()
    this.#computedExecutions = 0
  }
  /**
   * Executes the callback function after the period of time specified by <code>delay</code>
   */
  timeout () {
    this.#isInterval = false
    this.#setup()
    this.#id = this.#setTimeout()
  }
  /**
   * Repeatedly executes the callback function with a fixed time delay (specified by <code>delay</code>) between each execution
   */
  interval () {
    this.#isInterval = true
    this.#setup()
    this.#id = this.#setInterval()
  }

  /* Private methods */
  #setTimeout = function() {
    return window.setTimeout(
      () => {
        this.callback(...arguments)
        this.#stopTick()
        this.#computedTimeElapsed = this.#delay // Set timeElapsed to delay in case the user has switched tabs (which pauses requestAnimationFrame)
        this.#computedExecutions = 1
      },
      this.#delay,
      ...this.#parameters
    )
  }
  #setInterval = function() {
    return window.setInterval(
      () => {
        this.callback(...this.#parameters)
        this.#stopTick()
        this.#computedTimeElapsed = 0
        this.#computedExecutions++
        this.#startTick()
      },
      this.#delay,
    )
  }
  #setTimeElapsed = function() {
    const timeElapsed = Date.now() - this.#started
    this.#computedTimeElapsed = this.#isInterval
      ? timeElapsed - this.#delay * this.#computedExecutions
      : Math.min(timeElapsed, this.#delay)
  }
  #setTimeRemaining = function() {
    this.#computedTimeRemaining = this.#delay - this.#computedTimeElapsed
  }
  #tick = function() {
    this.#setTimeElapsed()
    this.#setTimeRemaining()
    if (this.#computedTimeElapsed < this.#delay) {
      this.#stopTick()
      this.#startTick()
    }
  }
  #startTick = function() {
    this.#tickId = window.requestAnimationFrame(this.#tick.bind(this))
  }
  #stopTick = function() {
    window.cancelAnimationFrame(this.#tickId)
  }
  #setup = function() {
    this.clear()
    this.#computedExecutions = 0
    this.#computedTimeElapsed = 0
    this.#computedTimeRemaining = this.#delay
    this.#started = Date.now()
    this.#startTick()
  }
}

/*
 * Fetchable.js
 * (c) 2019 Alex Vipond
 * Released under the MIT license
 */

/* Dependencies */

/* Util */

export default class Fetchable {
  /* Private properties */
  #computedFetching
  #computedResponse
  #computedError
  #fetchOptions

  constructor (resource, options = {}) {
    /* Options */

    /* Public properties */
    this.resource = resource

    /* Private properties */
    this.#computedFetching = false
    this.#computedResponse = {}
    this.#computedError = {}

    /* Dependency */
    this.#fetchOptions = options
  }

  /* Public getters */
  get fetching () {
    return this.#computedFetching
  }
  get response () {
    return this.#computedResponse
  }
  get responseJson () {
    try {
      return this.response.json()
    } catch {
      return {}
    }
  }
  get error () {
    return this.#computedError
  }
  get errorJson () {
    try {
      return this.error.json()
    } catch {
      return {}
    }
  }

  /* Public methods */
  setResource (resource) {
    this.resource = resource
    return this
  }
  fetch () {
    this.#computedFetching = true

    return fetch(this.resource, this.#fetchOptions)
      .then(response => {
        this.#computedResponse = response
        return this
      })
      .catch(error => {
        this.#computedError = error
        return this
      })
  }

  /* Private methods */
}

/*
 * Observable.js
 * (c) 2019 Alex Vipond
 * Released under the MIT license
 */

/* Dependencies */

/* Util */
import is from '../util/is'
import toNodeList from '../util/toNodeList'

export default class Observable {
  #supportedObserverTypes
  #onIntersect
  #onMutate
  #onResize
  #intersectionOptions
  #computedIntersection
  #computedMutation
  #computedResize

  constructor (elements, options = {}) {
    /* Options */
    this.#onIntersect = options.onIntersect
    this.#onMutate = options.onMutate
    this.#onResize = options.onResize

    /* Public properties */
    this.elements = toNodeList(elements)

    /* Private properties */
    this.#supportedObserverTypes = ['intersection', 'mutation', 'resize']

    /* Dependency */
    this.#intersectionOptions = this.#getIntersectionOptions(options)
    this.#computedIntersection = is.function(this.#onIntersect)
      ? this.#getIntersection(this.#intersectionOptions)
      : null
    this.#computedMutation = is.function(this.#onMutate)
      ? this.#getMutation()
      : null
    this.#computedResize = is.function(this.#onResize)
      ? this.#getResize()
      : null
  }

  /* Public getters */
  get intersection () {
    return this.#computedIntersection
  }
  get mutation () {
    return this.#computedMutation
  }
  get resize () {
    return this.#computedResize
  }

  /* Public methods */
  setElements (elements) {
    this.elements = toNodeList(elements)
    return this
  }
  observe (options = {}) {
    this.elements.forEach(element => {
      this.#supportedObserverTypes.forEach(observerType => {
        if (!is.null(this[`${observerType}`])) {
          this[`${observerType}`].observe(element, options)
        }
      })
    })
  }
  disconnect () {
    this.#supportedObserverTypes.forEach(observerType => {
      if (!is.null(this[`${observerType}`])) {
        this[`${observerType}`].disconnect()
      }
    })
  }
  takeRecords () {
    this.#supportedObserverTypes.forEach(observerType => {
      if (!is.null(this[`${observerType}`])) {
        this[`${observerType}`].takeRecords()
      }
    })
  }
  unobserve (element) {
    this.#supportedObserverTypes.forEach(observerType => {
      if (!is.null(this[`${observerType}`])) {
        this[`${observerType}`].unobserve(element)
      }
    })
  }

  /* Private methods */
  #getIntersectionOptions = ({ onIntersect, onMutate, onResize, ...rest }) => rest
  #getIntersection = function(options) {
    return new IntersectionObserver(this.#onIntersect, options)
  }
  #getMutation = function() {
    return new MutationObserver(this.#onMutate)
  }
  #getResize = function() {
    return new ResizeObserver(this.#onResize)
  }
}

/*
 * Poppable.js
 * (c) 2019 Alex Vipond
 * Released under the MIT license
 */

/* Dependencies */
import Dependency from '../wrappers/PoppablePopper'

/* Util */

export default class Poppable {
  #dependencyOptions
  #dependency

  constructor ({ reference, popper }, options = {}) {
    /* Options */

    /* Public properties */
    this.reference = reference
    this.popper = popper

    /* Private properties */

    /* Dependency */
    this.#dependencyOptions = this.#getDependencyOptions(options)
    this.#dependency = new Dependency({ reference: this.reference, popper: this.popper }, this.#dependencyOptions)
  }

  /* Public getters */
  get manager () {
    return this.#dependency.manager
  }

  /* Public methods */
  setReference (reference) {
    this.reference = reference
    return this
  }
  setPopper (popper) {
    this.popper = popper
    return this
  }
  update () {
    this.#dependency.update()
  }
  enableEventListeners () {
    this.#dependency.enableEventListeners()
  }
  disableEventListeners () {
    this.#dependency.disableEventListeners()
  }
  scheduleUpdate () {
    this.#dependency.scheduleUpdate()
  }
  destroy () {
    this.#dependency.destroy()
  }

  /* Private methods */
  #getDependencyOptions = (options) => options
}

/*
 * Searchable.js
 * (c) 2019 Alex Vipond
 * Released under the MIT license
 */

/* Dependencies */
import Dependency from '../wrappers/SearchableLunr.js'

/* Util */

export default class Searchable {
  #computedResults
  #dependencyOptions
  #dependency

  constructor (array, options = {}) {
    /* Options */
    options = {
      positionIsIncluded: false,
      itemIsIncluded: true,
      ...options
    }

    /* Public properties */
    this.array = array

    /* Private properties */
    this.#computedResults = []

    /* Dependency */
    this.#dependencyOptions = options
    this.#dependency = new Dependency(this.array, this.#dependencyOptions)
  }

  /* Public getters */
  get results () {
    return this.#computedResults
  }
  get index () {
    return this.#dependency.index
  }

  /* Public methods */
  setArray (array) {
    this.array = array
    this.#dependency = new Dependency(this.array, this.#dependencyOptions)
    return this
  }
  search (query) {
    this.#computedResults = this.#dependency.search(query)
    return this
  }

  /* Private methods */
}

/*
